import React, { useState, useEffect } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer() {
  // State to manage transactions
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch transactions from the backend API
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch("http://localhost:8001/transactions");
        const data = await response.json();
        setTransactions(data);
      } catch (error) {
        console.error("Error fetching transactions:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []); // The empty dependency array ensures that this effect runs once when the component mounts

  // Function to add a new transaction
  const handleAddTransaction = (newTransaction) => {
    // Assuming transactions have unique IDs for keys
    setTransactions([...transactions, { id: Date.now(), ...newTransaction }]);
  };

  // Function to filter transactions based on search term
  const handleSearch = (searchTerm) => {
    // Implement filtering logic here
    // For now, let's log the search term to the console
    console.log("Searching for:", searchTerm);
  };

  return (
    <div>
      <Search onSearch={handleSearch} />
      <AddTransactionForm onAddTransaction={handleAddTransaction} />
      {loading ? (
        <p>Loading transactions...</p>
      ) : (
        <TransactionsList transactions={transactions} />
      )}
    </div>
  );
}

export default AccountContainer;
