// ...

function AccountContainer() {
  // State to manage transactions
  const [transactions, setTransactions] = useState([]);
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch transactions from the backend API
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch("http://localhost:8001/transactions");
        const data = await response.json();
        setTransactions(data);
        setFilteredTransactions(data); // Initialize filteredTransactions with all transactions
      } catch (error) {
        console.error("Error fetching transactions:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  // Function to add a new transaction
  const handleAddTransaction = (newTransaction) => {
    setTransactions([...transactions, { id: Date.now(), ...newTransaction }]);
  };

  // Function to filter transactions based on search term
  const handleSearch = (searchTerm) => {
    const lowerCaseSearchTerm = searchTerm.toLowerCase();
    const filtered = transactions.filter((transaction) =>
      transaction.description.toLowerCase().includes(lowerCaseSearchTerm)
    );
    setFilteredTransactions(filtered);
  };

  // ...

  return (
    <div>
      <Search onSearch={handleSearch} />
      <AddTransactionForm onAddTransaction={handleAddTransaction} />
      {loading ? (
        <p>Loading transactions...</p>
      ) : (
        <TransactionsList transactions={filteredTransactions} />
      )}
    </div>
  );
}

